import React, { useState, useEffect, useMemo } from 'react';
import { useAddress, ConnectWallet, Web3Button, useContract, useNFTBalance } from '@thirdweb-dev/react';
const { contract: token } = useContract(process.env.TOKEN_ADDRESS, 'token');

const App = () => {
  const address = useAddress();
  console.log("üëã Address:", address);

  const editionDropAddress = process.env.EDITION_DROP_ADDRESS;
  const { contract: editionDrop } = useContract(editionDropAddress, "edition-drop");

  // Hook to check if the user has our NFT
  const { data: nftBalance } = useNFTBalance(editionDrop, address, "0");

  const hasClaimedNFT = useMemo(() => {  
    const [memberTokenAmounts, setMemberTokenAmounts] = useState([]);
    const [memberAddresses, setMemberAddresses] = useState([]);
    const shortenAddress = (str) => {
      return str.substring(0, 6) + '...' + str.substring(str.length - 4);
    };

    useEffect(() => {
      if (!hasClaimedNFT) {
        return;
      }
      const getAllAddresses = async () => {
        try {
          const memberAddresses = process.env.MEMBERS.split(',');
          setMemberAddresses(memberAddresses);
          console.log('üöÄ Members addresses', memberAddresses);
        } catch (error) {
          console.error('failed to get member list', error);
        }
      };
      getAllAddresses();
    }, [hasClaimedNFT, memberAddresses]);

    useEffect(() => {
      if (!hasClaimedNFT) {
        return;
      }
      
      const getAllBalances = async () => {
        try {
          const amounts = await token?.history.getAllHolderBalances();
          setMemberTokenAmounts(amounts);
          console.log('üëú Amounts', amounts);
        } catch (error) {
          console.error('failed to get member balances', error);
        }
      };
      getAllBalances();
    }, [hasClaimedNFT, token?.history]);

    const memberList = useMemo(() => {
      return memberAddresses.map((address) => {
        const member = memberTokenAmounts?.find(({ holder }) => holder === address);
    
        return {
          address,
          tokenAmount: member?.balance.displayValue || '0',
        };
      });
    }, [memberAddresses, memberTokenAmounts]);
  }, [nftBalance]);

  if (!address) {
    return (
      <div className="landing">
        <h1>Welcome to DemonSlayerDAO</h1>
        <div className="btn-hero">
          <ConnectWallet />
        </div>
      </div>
    );
  }

  if (hasClaimedNFT) {
    return (
      <div className="member-page">
        <h1>üç™DAO Member Page</h1>
        <p>Congratulations on being a member</p>
        <div>
          <div>
            <h2>Member List</h2>
            <table className="card">
              <thead>
                <tr>
                  <th>Address</th>
                  <th>Token Amount</th>
                </tr>
              </thead>
              <tbody>
                {memberList.map((member) => {
                  return (
                    <tr key={member.address}>
                      <td>{shortenAddress(member.address)}</td>
                      <td>{member.tokenAmount}</td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="mint-nft">
      <h1>Mint your free üç™DAO Membership NFT</h1>
      <div className="btn-hero">
        <Web3Button 
          contractAddress='0x8ed15E768DF13eD6808dc1220Bc94b11bFaC31f9'
          action={contract => {
            contract.erc1155.claim(0, 1)
          }}
          onSuccess={() => {
            alert(`üåä Successfully Minted! Check it out on OpenSea: https://testnets.opensea.io/assets/${editionDropAddress}/0`);
          }}
          onError={error => {
            console.log("Failed to mint NFT", error);
          }}
        >
          Mint your NFT 
        </Web3Button>
      </div>
    </div>
  );
}

export default App;
